INSERT INTO `ps_footercontent_lang` (`id_content`, `id_lang`, `body_title`, `body_paragraph`) VALUES
(1, 1, 'Интернет-магазин Lamplus', '<p>ООО «Лампа», реализует оптом и в розницу светильники интерьерного и экстерьерного направления, лампы и другие источники освещения. Предлагаем продукцию европейских производителей: AGMA LUX Италия, ALMALIGHT Испания, ALMERICH Испания, ALT LUCIALTERNATIVE Италия, ANTONANGELI Италия, AQUA CREATIONS Израиль, ARCHEO VENICE DESIGN Италия, ARTEMIDE Италия, ARTURO ALVARES Испания, AUGENTI Италия, AURELIANO TOSO Италия, AXO LIGHT Италия, BADARI Италия, BEBY GROUP Италия, BEJORAMA Испания, BLITZ Германия, BRAND VAN EGMOND BV Нидерланды, CATELLANI&SMITH Италия, CONTEMPORANEA Италия, CZECH REPUBLIC, DE MAJO Италия, DISENO Испания, EDI LIGHT Австрия, EGLO Австрия, EGOLUCE Италия, EL TORRENT Испания, EMME PI LIGHT Италия, ESEDRA Италия, ESEO Бельгия, EURO LAMP ART Италия, FABBIAN Италия, FERROLUCE Италия, FLOS Италия, FM LLUMINACION Испания, FOSCARINI Италия, GALLERY VETRI D''ARTE Италия, GAMMADELTAGROUP Италия, GUMARCRIS Испания, IDEAL LUX Италия, IL PARALUME MARINA Италия, ILAS Италия, ILLUMINATI, ITRE Италия, JISO Испания, KANLUX Польша, KREA DESIGN Италия, LA LAMPADA Италия, LAMPADARI Италия, LAMPARA SCHULLER Испания, LINEA LIGHT Италия, LUSSOLE Италия, MAGNUM, MARKSLOJD Швеция, MARTINEZ ORTS Испания, MASCA Италия, MASIERO Италия, MASSIVE Бельгия, MECHINI Италия, METAL SPOT Италия, MOOOI Голландия, MORETTI LUCE Италия, MURANO DUE Италия, NERVILAMP Италия, OSRAM Германия, PHILIPS Голландия, PILA Польша, POSSONI Италия, PRANDINA Италия, RECCAGNI ANGELO Италия, RIPERLAMP Испания, ROGU Испания, RUGGIU Италия, SAVOY HOUSE Испания, SFORZIN Италия, SIRU SAS Италия, STUDIO ITALIA DESIGN Италия, SWAROVSKI, VISTOSI Италия, VOLTOLINA Италия, WUNDERLICHT Германия, ZONCA Италия. Мы постоянно расширяем ассортимент и готовы предоставить лучший сервис нашим клиентам.<br /><br />На сегодняшний день в состав компании входит два подразделения по работе с оптовыми и розничными покупателями. Отдел оптовых продаж обслуживает строительные организации, архитектурные бюро и дизайнерские агентства. Для розничных клиентов предоставляем широкий ассортимент, доставку заказов, инсталяцию и сервисное обслуживание оборудования. Высокая скорость обработки информации и доставки заказов позволяет обслуживать базу клиентов с высокой эффективностью, предоставляя максимум возможностей для комплектации объектов строительства в кратчайшие сроки. Максимальный сервис и комфорт для заказчиков обеспечивается за счет комплексных поставок электротехнических изделий и освещения. Компания осуществляет деятельность на всей территории Украины.</p>'),
